package controlflow;

public class NestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		
		
		
		if (a ==10) {
			
			if (b == 20)
			{
				System.out.println("Inside IF");
								
			}
			else {
				System.out.println("InsideElse");
			
			}
			
					
		}
		else {
			System.out.println("Outside Else");
			
		} 
			
			
			
		
		
	}

}
